function lngEx(){
    global $db;
    if(mb_strpos($_SERVER[REQUEST_URI],dirname($_SERVER[PHP_SELF]),null,'utf-8')!==false){
           $_SERVER[REQUEST_URI]=mb_substr($_SERVER[REQUEST_URI],mb_strlen(dirname($_SERVER[PHP_SELF]),'utf-8'),mb_strlen($_SERVER[REQUEST_URI],'utf-8'),'utf-8'); 
    }
        
    $d=ltrim($_SERVER[REQUEST_URI],'/');
    
    $r=explode('/',$d);
    
    if(in_array($r[0],$db->getLng())){
        array_shift($r);
        return (!empty($r)?'/':null).implode('/',$r);
    }else{
        return !empty($d)?'/'.$d:null;
    } 
}

function extr($data){
	foreach($data as $k=>$g){
		if(is_array($data[$k])){
			extr($data[$k]);
		}else{
			$data[$k]=mysql_real_escape_string(htmlspecialchars(strip_tags($data[$k])));
		}
	}
	return $data;
}

function xss_clean($data)
{
	// Fix &entity\n;
	$data = str_replace(array('&amp;','&lt;','&gt;'), array('&amp;amp;','&amp;lt;','&amp;gt;'), $data);
	$data = preg_replace('/(&#*\w+)[\x00-\x20]+;/u', '$1;', $data);
	$data = preg_replace('/(&#x*[0-9A-F]+);*/iu', '$1;', $data);
	

	// Remove any attribute starting with "on" or xmlns
	$data = preg_replace('#(<[^>]+?[\x00-\x20"\'])(?:on|xmlns)[^>]*+>#iu', '$1>', $data);

	// Remove javascript: and vbscript: protocols
	$data = preg_replace('#([a-z]*)[\x00-\x20]*=[\x00-\x20]*([`\'"]*)[\x00-\x20]*j[\x00-\x20]*a[\x00-\x20]*v[\x00-\x20]*a[\x00-\x20]*s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:#iu', '$1=$2nojavascript...', $data);
	$data = preg_replace('#([a-z]*)[\x00-\x20]*=([\'"]*)[\x00-\x20]*v[\x00-\x20]*b[\x00-\x20]*s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:#iu', '$1=$2novbscript...', $data);
	$data = preg_replace('#([a-z]*)[\x00-\x20]*=([\'"]*)[\x00-\x20]*-moz-binding[\x00-\x20]*:#u', '$1=$2nomozbinding...', $data);

	// Only works in IE: <span style="width: expression(alert('Ping!'));"></span>
	$data = preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?expression[\x00-\x20]*\([^>]*+>#i', '$1>', $data);
	$data = preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?behaviour[\x00-\x20]*\([^>]*+>#i', '$1>', $data);
	$data = preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:*[^>]*+>#iu', '$1>', $data);

	// Remove namespaced elements (we do not need them)
	$data = preg_replace('#</*\w+:\w[^>]*+>#i', '', $data);

	do
	{
			// Remove really unwanted tags
			$old_data = $data;
			$data = preg_replace('#</*(?:applet|b(?:ase|gsound|link)|embed|frame(?:set)?|i(?:frame|layer)|l(?:ayer|ink)|meta|object|s(?:cript|tyle)|title|xml)[^>]*+>#i', '', $data);
	}
	while ($old_data !== $data);

	return $data;
}


function clear($data,$type=0)
{
	global $db;
	if($type==1)
	{
	   $data=abs((int)$data);
	}
	else{
		$data=mysql_real_escape_string(strip_tags(trim($data)));
	}
	return $data;
}
    
function cleanData($data)
{
	$data=mysql_real_escape_string($data);
	$data=htmlentities($data, ENT_QUOTES);
	$data=trim($data);
	$data=stripcslashes($data);
	$data=htmlspecialchars($data);
	$data=strip_tags($data);
	return $data;
}
    
function cleanInput($input){
	$search = array( '@<script[^>]*?>.*?</script>@si',  '@<[\/\!]*?[^<>]*?>@si', '@<style[^>]*?>.*? </style>@siU', '@<![\s\S]*?--[ \t\n\r]*>@');    
	$output = preg_replace($search, '', $input);
	return $output;
}
    
function sanitize($input) { 
   if (is_array($input)) { foreach($input as $var=>$val) { $output[$var] = sanitize($val); } } 
   else { if (get_magic_quotes_gpc()) { $input = stripslashes($input); } $input  = cleanInput($input); $output = mysql_real_escape_string($input); } 
   return $output; 
}    

