	function probel($t){
		$a=mb_split('\s',$t);
		array_pop($a);
		return implode(' ',$a);
	}
	
	function probel_alx($num,$text,$url,$stl){
		if(strlen($text)>$num){
			$limit=substr($text,0,$num);
			for($x=-6;$x>-35;$x--){
				if(substr($limit,$x-1,$x)===" "){
					$res=substr($limit,0,$x-1);
					$res.="...<a href='$url' style='$stl'>>>></a>";
					break;
				}
			}
		}else{
			$res=$text."...<a href='$url' style='$stl'>>>></a>";
		}
		return $res;
	}
	
	function probel_alx2($num, $text, $stl){
		if(strlen($text)>$num){
			$limit=substr($text,0,$num);
			for($x=-6;$x>-35;$x--){
				if(substr($limit,$x-1,$x)===" "){
					$res=substr($limit,0,$x-1);
					$res.="...";
					break;
				}
			}
		}else{
			$res=$text."...";
		}
		return $res;
	}
	
