					news_numbers_link_td_print($type,$pn, $id, 0, $kacpage);
				}
				else
				{
					echo' <a href="?options='.$type.'&id='.$id.'&pn=0">'.$lang_numbers_first[$lang].'</a> ';
						 
					If($pn<ceil($count_links/2))
					{
						news_numbers_link_td_print($type,$pn, $id, 0, $count_links);
					}
					elseif($pn>=$kacpage-ceil($count_links/2))
					{
						news_numbers_link_td_print($type,$pn, $id, $kacpage-$count_links, $kacpage);
					}
					else
					{
						news_numbers_link_td_print($type,$pn, $id, $pn-ceil($count_links/2)+1, $pn+ceil($count_links/2));
					}
					
					echo'<a href="?options='.$type.'&id='.$id.'&pn='.($kacpage-1).'">'.$lang_numbers_last[$lang].'</a> ';
				
				}
				
			}
			
