function content_first($id_page, $type) {
global $db,$lang, $page_cat, $actions;
	$Sql = $db->query("Select * From ".TABLE_PAGES." Where id='$id_page' AND lang_id='$lang'");	
	$Row = $db->fetchArray($Sql);
	$first = strtok($Row[$type], ".");
	
	$search = array ("'<script[^>]*?>.*?</script>'si",  // Вырезает javaScript
                 "'<[\/\!]*?[^<>]*?>'si",           // Вырезает HTML-теги
                 "'([\r\n])[\s]+'",                 // Вырезает пробельные символы
                 "'&(quot|#34);'i",                 // Заменяет HTML-сущности
                 "'&(amp|#38);'i",
                 "'&(lt|#60);'i",
                 "'&(gt|#62);'i",
                 "'&(nbsp|#160);'i",
                 "'&(iexcl|#161);'i",
                 "'&(cent|#162);'i",
                 "'&(pound|#163);'i",
                 "'&(copy|#169);'i",
                 "'&#(\d+);'e");                    // интерпретировать как php-код

	$replace = array ("",
					  "",
					  "\\1",
					  "\"",
					  "&",
					  "<",
					  ">",
					  " ",
					  chr(161),
					  chr(162),
					  chr(163),
					  chr(169),
					  "chr(\\1)");

	$first = preg_replace($search, $replace, $first);
	
	If(substr($first, 0, 1) == '<' or substr($first, 1, 1) == '<' or substr($first, 2, 1) == '<' or substr($first, 3, 1) == '<')
	{
		$first = "";
	}
	
	return $first;
}

