function jResize($tmp,$path,$thumb_width=200,$thumb_height=150){
	
	$image = imagecreatefromjpeg($tmp);
	$width = imagesx($image);
	$height = imagesy($image);

	$original_aspect = $width / $height;
	$thumb_aspect = $thumb_width / $thumb_height;

	if ( $original_aspect >= $thumb_aspect )
	{
	   $new_height = $thumb_height;
	   $new_width = $width / ($height / $thumb_height);
	}
	else
	{
	   $new_width = $thumb_width;
	   $new_height = $height / ($width / $thumb_width);
	}
	$thumb = imagecreatetruecolor( $thumb_width, $thumb_height );

	imagecopyresampled($thumb,$image, 0 - ($new_width - $thumb_width) / 2, 0 - ($new_height - $thumb_height) / 2,0, 0,$new_width, $new_height, $width, $height);
	imagejpeg($thumb, $path, 100);
}


function jResize2($tmp,$name){
	copy($_FILES,"Photos/".$_FILES["userfile"]["name"]);
	$width=500; //*** Fix Width & Heigh (Autu caculate) ***//
	$size=GetimageSize($tmp);
	$height=round($width*$size[1]/$size[0]);
	$images_orig = ImageCreateFromJPEG($tmp);
	$photoX = ImagesX($images_orig);
	$photoY = ImagesY($images_orig);
	$images_fin = ImageCreateTrueColor($width, $height);
	ImageCopyResampled($images_fin, $images_orig, 0, 0, 0, 0, $width+1, $height+1, $photoX, $photoY);
	ImageJPEG($images_fin,"Photos/".$name);
	ImageDestroy($images_orig);
	ImageDestroy($images_fin);
}


